import React , {useState} from "react"
import { ProSidebar, Menu, MenuItem, SubMenu } from 'react-pro-sidebar';
import 'react-pro-sidebar/dist/css/styles.css';
import { useNavigate } from "react-router-dom"
import { useAuth } from "../contexts/AuthContext"
import Alert from 'react-popup-alert'

export default function Sidebar() {

    const { currentUser, logout } = useAuth()
    const navigate = useNavigate();

    const [alert, setAlert] = React.useState({
        type: 'error',
        text: 'This is a alert message',
        show: false
      })

    async function handleLogout() {
        

        try {
        await logout()
        navigate('/login');
        } catch {

            onShowAlert('error')
       
        }
    }

    function onCloseAlert() {
        setAlert({
          type: '',
          text: '',
          show: false
        })
      }
    
      function onShowAlert(type) {
        setAlert({
          type: type,
          text: 'Logout is failed!',
          show: true
        })
      }

    const [menuCollapse, setMenuCollapse] = useState(false)

    //create a custom function that will change menucollapse state from false to true and true to false
    const menuIconClick = () => {
        //condition checking to change state from true to false and vice versa
        menuCollapse ? setMenuCollapse(false) : setMenuCollapse(true);
    };


    return (
        <>
                    <ProSidebar collapsed={menuCollapse} style={{background: "#663d00"  , borderColor: "#663d00"}}>
          <SidebarHeader>
          <div className="logotext">
              {/* small and big change using menucollapse state */}
              <p>{menuCollapse ? "Dash" : "Dashboard"}</p>
            </div>
            <div className="closemenu" onClick={menuIconClick}>
                {/* changing menu collapse icon on click */}
              {menuCollapse ? (
                <FiArrowRightCircle/>
              ) : (
                <FiArrowLeftCircle/>
              )}
            </div>
          </SidebarHeader>
          <SidebarContent>
            <Menu iconShape="square">
              <MenuItem active={true} icon={<FiHome />}>
                <Link to="/home" >Home</Link>
              </MenuItem>
              <MenuItem icon={<FaList />}><Link to="/summarizer" >Summarizer</Link></MenuItem>
              <MenuItem icon={<RiPencilLine />}><Link to="/predict" >Predict</Link></MenuItem>
            </Menu>
          </SidebarContent>
          <SidebarFooter>
            <Menu iconShape="square">
              <MenuItem icon={<FiLogOut />}>
                <div className="w-100 text-center mt-2">
                    <Button variant="link" onClick={handleLogout} style={{background: "black"  , borderColor: "black"}}>
                        Log Out
                    </Button>
                </div>
              </MenuItem>
            </Menu>
          </SidebarFooter>
        </ProSidebar>
        <Alert
        header={'Error'}
        btnText={'Close'}
        text={alert.text}
        type={alert.type}
        show={alert.show}
        onClosePress={onCloseAlert}
        pressCloseOnOutsideClick={true}
        showBorderBottom={true}
        alertStyles={{}}
        headerStyles={{}}
        textStyles={{}}
        buttonStyles={{}}
    />
        </>

    );

}
